import { fileURLToPath } from 'node:url';
import { join } from 'pathe';
import { defu } from 'defu';
import { defineNuxtModule, addImportsDir, addPlugin } from '@nuxt/kit';

const name = "nuxt-gtag";
const version = "0.3.0";

const module = defineNuxtModule({
  meta: {
    name,
    version,
    configKey: "gtag",
    compatibility: {
      nuxt: "^3"
    }
  },
  defaults: {
    id: void 0,
    config: void 0
  },
  setup(options, nuxt) {
    nuxt.options.runtimeConfig.public.gtag = defu(
      nuxt.options.runtimeConfig.public.gtag,
      options
    );
    const runtimeDir = fileURLToPath(new URL("./runtime", import.meta.url));
    nuxt.options.build.transpile.push(runtimeDir);
    addImportsDir(join(runtimeDir, "composables"));
    addPlugin({
      src: join(runtimeDir, "plugin.client"),
      mode: "client"
    });
  }
});

export { module as default };
